import React from 'react';
import styled from 'styled-components';

function ExpenseItem(props) {
	return (
		<Wrapper>
			<Date>
				<h2>{props.date.toLocaleString('default', { month: 'long' })}</h2>
				<h3>{props.date.getFullYear()}</h3>
				<h1>{props.date.getDay()}</h1>
			</Date>
			<Item>{props.item}</Item>
			<Cost>{props.cost}</Cost>
		</Wrapper>
	);
}

export default ExpenseItem;

const Wrapper = styled.div`
	width: 70%;
	display: flex;
	justify-content: center;
	align-items: center;
	background-color: #f4abc4;
	border-radius: 10px;
	margin: 10px;
`;

const Date = styled.div`
	flex: 1;
	margin: 10px;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	border: 2px solid white;
	color: white;
	background-color: #0f0f0f;
	padding: 0.2px 10px;
	border-radius: 10px;

	h1 {
		margin: 2px;
		font-size: 20px;
		font-weight: 400;
	}
	h2 {
		margin: 2px;
		font-size: 16px;
		font-weight: 400;
	}
	h3 {
		margin: 2px;
		font-size: 10px;
		font-weight: 200;
	}
`;

const Item = styled.div`
	flex: 4;
	display: flex;
	justify-content: flex-start;
	align-items: center;
`;

const Cost = styled.div`
	flex: 1;
	display: flex;
	justify-content: center;
	align-items: center;
	border: 5px solid white;
	color: white;
	background-color: #7e7474;
	padding: 5px 10px;
	border-radius: 10px;
	margin: 10px;
`;
